using MedicalEngineMicroService.Common.Extensions;
using MedicalEngineMicroService.Core.Models.Entities.Tariffs;
using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.Metadata.Builders;

namespace MedicalEngineMicroService.Persistence.EntityConfigurations.Tariffs {
    public class MedicalItemPublicationConfiguration : IEntityTypeConfiguration<MedicalItemPublication> {
        public void Configure (EntityTypeBuilder<MedicalItemPublication> builder) {
            builder.ConfigureLookupDescription ();

            builder.HasIndex (medicalItem => new { medicalItem.MedicalItemId, medicalItem.PublicationId })
                .IsUnique (true);

            builder.Property (medicalItem => medicalItem.Description).IsRequired ();

            builder.Property (medicalItem => medicalItem.Quantity).HasDefaultValue (1.00);

            SeedMedicalItemPublication (builder);

        }

        private void SeedMedicalItemPublication (EntityTypeBuilder<MedicalItemPublication> modelBuilder) {
            modelBuilder.HasData (
                new MedicalItemPublication { Id = 1, MedicalItemId = 1, PublicationId = 2, Description = @"Written report on X-rays: The lowest level code for a new patient office (consulting rooms) visit, is applicable only where a radiologist is requested to give a written report on X-rays taken elsewhere and submitted to him. The above mentioned item and the lowest level initial hospital visit code, as appropriate are not to be used for routine reporting of X-rays taken elsewhere", NormalizedDescription = @"WRITTENREPORTONX-RAYS:THELOWESTLEVELCODEFORANEWPATIENTOFFICE(CONSULTINGROOMS)VISIT,ISAPPLICABLEONLYWHEREARADIOLOGISTISREQUESTEDTOGIVEAWRITTENREPORTONX-RAYSTAKENELSEWHEREANDSUBMITTEDTOHIM.THEABOVEMENTIONEDITEMANDTHELOWESTLEVELINITIALHOSPITALVISITCODE,ASAPPROPRIATEARENOTTOBEUSEDFORROUTINEREPORTINGOFX-RAYSTAKENELSEWHERE" },
                new MedicalItemPublication { Id = 2, MedicalItemId = 2, PublicationId = 2, Description = @"Procedures performed in own procedure rooms: Procedures performed in doctors' own procedure rooms instead of in a hospital theatre or unattached theatre unit: as per fee for procedure + 100% (the value of modifier 0004 equals 100% of the value of the procedure performed). See Section V (Section G in SAMA's DBT) for a list of procedures, which are often done in rooms to which Modifier 0004 should not be applied. Please note: Only the medical practitioner who owns the facility and the equipment may charge modifier 0004. Only one person may claim this modifier for procedures performed in doctors' own procedure rooms", NormalizedDescription = @"PROCEDURESPERFORMEDINOWNPROCEDUREROOMS:PROCEDURESPERFORMEDINDOCTORS'OWNPROCEDUREROOMSINSTEADOFINAHOSPITALTHEATREORUNATTACHEDTHEATREUNIT:ASPERFEEFORPROCEDURE+100%(THEVALUEOFMODIFIER0004EQUALS100%OFTHEVALUEOFTHEPROCEDUREPERFORMED).SEESECTIONV(SECTIONGINSAMA'SDBT)FORALISTOFPROCEDURES,WHICHAREOFTENDONEINROOMSTOWHICHMODIFIER0004SHOULDNOTBEAPPLIED.PLEASENOTE:ONLYTHEMEDICALPRACTITIONERWHOOWNSTHEFACILITYANDTHEEQUIPMENTMAYCHARGEMODIFIER0004.ONLYONEPERSONMAYCLAIMTHISMODIFIERFORPROCEDURESPERFORMEDINDOCTORS'OWNPROCEDUREROOMS" },
                new MedicalItemPublication {
                    Id = 3, MedicalItemId = 3, PublicationId = 2, Description = @"Multiple therapeutic procedures/operations under the same anaesthetic:\r\na) Unless otherwise identified in the tariff when multiple therapeutic procedures/operations add significant time and/or complexity, and when each procedure/operation is clearly identified and defined, the following values shall prevail:  100% (full value) for the first or major procedure/operation, 75% for the second procedure/operation, 50% for the third procedure/operation, 25% for the fourth and subsequent procedures/operations. This modifier does not apply to purely diagnostic procedures.\r\nb) In the case of multiple fractures and/or dislocations the above values shall prevail.\r\nc) When purely diagnostic endoscopic procedures or diagnostic endoscopic procedures unrelated to any therapeutic procedures performed, are performed under the same general anaesthetic, Modifier 0005 is not applicable to the fees for such diagnostic endoscopic procedures as the fees for endoscopic procedures do not provide for after-care. Specify unrelated endoscopic procedure and provide diagnosis to indicate diagnostic endoscopic procedure(s) unrelated to other (therapeutic) procedures performed under the same anaesthetic.\r\nd) Please note:  When more than one small procedure is performed and the tariff makes provision for items for ""subsequent"" or ""maximum for multiple additional procedures"" (see Section 2. Integumentary System) Modifier 0005 is not applicable as the fee is already a reduced fee.\r\ne) ?+? Means that this item is used in addition to another definitive procedure and is therefore not subject to reduction according to Modifier 0005 (see also Modifier 0082)", NormalizedDescription = @"
                    MULTIPLETHERAPEUTICPROCEDURES / OPERATIONSUNDERTHESAMEANAESTHETIC: \R\ NA) UNLESSOTHERWISEIDENTIFIEDINTHETARIFFWHENMULTIPLETHERAPEUTICPROCEDURES / OPERATIONSADDSIGNIFICANTTIMEAND / ORCOMPLEXITY, ANDWHENEACHPROCEDURE / OPERATIONISCLEARLYIDENTIFIEDANDDEFINED, THEFOLLOWINGVALUESSHALLPREVAIL : 100 % (FULLVALUE) FORTHEFIRSTORMAJORPROCEDURE / OPERATION, 75 % FORTHESECONDPROCEDURE / OPERATION, 50 % FORTHETHIRDPROCEDURE / OPERATION, 25 % FORTHEFOURTHANDSUBSEQUENTPROCEDURES / OPERATIONS.THISMODIFIERDOESNOTAPPLYTOPURELYDIAGNOSTICPROCEDURES.\R\ NB) INTHECASEOFMULTIPLEFRACTURESAND / ORDISLOCATIONSTHEABOVEVALUESSHALLPREVAIL.\R\ NC) WHENPURELYDIAGNOSTICENDOSCOPICPROCEDURESORDIAGNOSTICENDOSCOPICPROCEDURESUNRELATEDTOANYTHERAPEUTICPROCEDURESPERFORMED,
        AREPERFORMEDUNDERTHESAMEGENERALANAESTHETIC,
        MODIFIER0005ISNOTAPPLICABLETOTHEFEESFORSUCHDIAGNOSTICENDOSCOPICPROCEDURESASTHEFEESFORENDOSCOPICPROCEDURESDONOTPROVIDEFORAFTER - CARE.SPECIFYUNRELATEDENDOSCOPICPROCEDUREANDPROVIDEDIAGNOSISTOINDICATEDIAGNOSTICENDOSCOPICPROCEDURE (S) UNRELATEDTOOTHER (THERAPEUTIC) PROCEDURESPERFORMEDUNDERTHESAMEANAESTHETIC.\R\ ND) PLEASENOTE : WHENMORETHANONESMALLPROCEDUREISPERFORMEDANDTHETARIFFMAKESPROVISIONFORITEMSFOR ""
